let scId=0;function start(){Shelly.call("KVS.Get",{key:"schedulerIDs"+scId},(function(e,l,d,c){e&&delSc(JSON.parse(e.value))}))}function delSc(e){Shelly.call("Schedule.Delete",{id:e},(function(e,l,d,c){0!==l?print("Script #"+scId,"schedule ",c.id," deletion by watchdog failed."):print("Script #"+scId,"schedule ",c.id," deleted by watchdog."),delKVS()}),{id:e})}function delKVS(){Shelly.call("KVS.Delete",{key:"schedulerIDs"+scId})}Shelly.addStatusHandler((function(e){"script"!==e.name||e.delta.running||(scId=e.delta.id,start())}));